import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { IconButton,FormControl } from "@mui/material";
import { Visibility,VisibilityOff } from "@mui/icons-material";
import { InputLabel ,InputAdornment,OutlinedInput,Divider} from "@mui/material";
import {useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function Login() {
  const [showPassword, setShowPassword] = useState(false);
  const [data,setData]=useState({
    email:'',
    password:'',
  })
  
  const navigate=useNavigate()

  const handledInputChange=(e)=>{
    const {name,value}=e.target
    setData((previousData)=>({
      ...previousData,
      [name]:value
    }))
    // console.log(data)
  }
  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  const signUpPage=()=>{
    navigate('/signup')
  }
  const homePage=async()=>{
    // const User={email:state.email, password:state.password}
    // console.log(User)
    // const existingUsers =JSON.parse(localStorage.getItem('Users')) || [];
    // console.log(existingUsers)
    // const user = existingUsers.find(user => 
    //   user.email === data.email && user.password === data.password 
    // );
    // console.log(user)
    // const fName=existingUsers.find(user=>user.firstName===data.firstName)
    // console.log(fName)
      // localStorage.setItem('currentUser',JSON.stringify(user))
    try{
      const response=await axios.post('http://localhost:8000/login',data)
      console.log(response)
      localStorage.setItem('token',response.data.token)
      navigate('/home')
      localStorage.getItem('token')
      await axios.get('http://localhost:8000/read')
    }catch(err){
        console.error(err)
      }
  }
  return (
    <Box sx={{display:'flex', justifyContent:'flex-end',alignItems:'center', minHeight:'100vh', p:2}} >
      <Box 
      component="form"
      sx={{
      textAlign:'center',
      width:'100%',
      maxWidth:'600px',
      p:3,
      display:"flex",
      alignItems:"center",
      justifyContent:"center",
      flexDirection:'column',
      border: '2px solid grey',
      borderRadius:'20px',
      bgcolor:'#cfd8dc' }}>
        
          <h3 >Log In</h3>
        <FormControl sx={{ m:1,width: "100%" }} >
          <TextField
            id="outlined-email-input"
            label="Email Address"
            name="email"
            value={data.email}
            onChange={handledInputChange}
          />
        </FormControl>
        <FormControl sx={{ m:1,width: "100%" }} variant="outlined" >
          <InputLabel htmlFor="outlined-adornment-password">
            Password
          </InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            name="password"
            value={data.password}
            onChange={handledInputChange}
            type={showPassword ? "text" : "password"}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
        </FormControl>
        <Button variant="contained" sx={{m:1,width: "100%",borderRadius:'20px' }}
        onClick={()=>homePage()}>
          Login
        </Button>
        <p style={{ color:'blue',m:1}}>Forgot Password?</p>
          <Divider></Divider>
        <Button variant="contained"  sx={{ m:1, width: "100%",borderRadius:'20px',bgcolor:'#81c784' }}
        onClick={()=>{signUpPage()}}>
          Create New Account
        </Button>
       
      </Box>
    </Box>
  );
}
