import React from 'react';
import Layout from '../components/Layout';
import { Box } from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import { useSelector, useDispatch } from 'react-redux'
import { addDetails } from './employeeSlice';
import PopUps from '../components/PopUps';

const columns = [
    { field: 'id', headerName: 'ID', width: 90 },
    { field: 'name', headerName: 'Name', width: 150 },
    { field: 'salary', headerName: 'Salary', width: 150 },
  ];

export default function Employee() {
    const state = useSelector((state) => state.employee);
    
    const rows = state.map((employee, index) => ({
        id: index + 1,
        name: employee.name,
        salary: employee.salary
      }));
  return (
    <Box sx={{ paddingLeft: 28, paddingTop: 10 }}>
      <Layout />
      <Box sx={{ display: 'flex', justifyContent: 'space-between', paddingRight:5 }} >
      <h3>Employees</h3>
      <PopUps/>
      </Box>
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={5}
        checkboxSelection
      />
    </Box>
  );
}
