import { Button } from "@mui/material";
import { createSlice } from "@reduxjs/toolkit";
import { Delete, Add} from "../components/PopUps";
import { useEffect } from "react";

const initialState={
    columns :[
        { field: 'id', headerName: 'ID', width: 70,headerAlign:'center', align:'center' },
        { field: 'SrNo', headerName: 'SrNo', width: 70,headerAlign:'center', align:'center' },
        { field: 'name', headerName: 'Name', width: 130, headerAlign:'center',align:'center' },
        { field: 'salary', headerName: 'Salary', type: 'number', width: 130,headerAlign:'center', align:'center' },
        {
          field: 'action',
          headerName: 'Action',
          type: 'number',
          width: 300,
          headerAlign:'center',
          align:'center',
          renderCell: (params) => (
            <Button
              variant="contained"
              color="primary"
            //   onClick={() => handleAction(params.row)}
            >
              <Add employeeID={params.row.id}/>
              <Delete employeeID={params.row.id}/>
            </Button>
          )
        },
      ],
      
    rows :[],
}
export const employeeSlice=createSlice({
    name:'Work',
    initialState,
    reducers:{
      addDetails: (state, action) => {
        state.rows.push(action.payload);
        // Ensure employees have sequential SrNo
        state.rows.forEach((employee, index) => {
          employee.SrNo = index + 1;
        });
      },
      deleteDetails: (state, action) => {
        const id=action.payload;
        // console.log(id)
        state.rows=state.rows.filter(employee=> employee.id !== id)
        
        state.rows.forEach((employee,index)=>{
          employee.SrNo=index+1;
        })
      },
      updateDetails: (state, action) => {
        const {id,updatedEmployee }= action.payload;
        console.log(updatedEmployee)
        const index = state.rows.findIndex(employee => employee.id === id);
        if (index !== -1) {
          state.rows[index] = {...state.rows[index],...updatedEmployee}
        }

      }
  
    }
  })
export const {addDetails,deleteDetails,updateDetails}=employeeSlice.actions
export default employeeSlice.reducer
