import React from "react";
import PropTypes from "prop-types";
import { styled, css } from "@mui/system";
import { Modal as BaseModal } from "@mui/base/Modal";
import Fade from "@mui/material/Fade";
import { Button, TextField } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import { IconButton } from "@mui/material";
import Checkbox from "@mui/material/Checkbox";
import { FormControlLabel } from "@mui/material";
import { useDispatch } from "react-redux";
import { addDetails, deleteDetails, updateDetails,toggleSnackbar } from "../pages/employeeSlice"; // Update import
import { v4 as uuidv4 } from "uuid";
import Snackbar from "@mui/material/Snackbar";
export const Add = ({
  option,
  employeeID,
  isActive,
  initialEmployeeDetails,
}) => {
  const [openAdd, setOpenAdd] = React.useState(false);
  const [employeeDetails, setEmployeeDetails] = React.useState({
    id: null,
    SrNo: 0,
    name: "",
    salary: "",
    isActive: isActive,
  });
  const [snackbarOpen, setSnackbarOpen] = React.useState(false); // Local state for Snackbar
  const dispatch = useDispatch();

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') return;
    setSnackbarOpen(false); // Close Snackbar locally
  };

  const handleOpenAdd = () => {
    setOpenAdd(true);
    !option && initialEmployeeDetails && (
      setEmployeeDetails(initialEmployeeDetails)
    );
  };

  const handleCloseAdd = () => setOpenAdd(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEmployeeDetails({
      ...employeeDetails,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = () => {
    const newEmployeeDetail = { ...employeeDetails, id: uuidv4() };
    if (option) {
      dispatch(addDetails(newEmployeeDetail));
      dispatch(toggleSnackbar("Employee Added"));
    } else {
      dispatch(updateDetails({ id: employeeID, updatedEmployee: newEmployeeDetail }));
      dispatch(toggleSnackbar("Employee Updated"));
    }
    setEmployeeDetails({
      SrNo: 0,
      id: uuidv4(),
      name: "",
      salary: "",
      isActive: isActive,
    });
    handleCloseAdd();
  };

  const action = (
    <React.Fragment>
      <IconButton size="small" aria-label="close" color="inherit" onClick={handleClose}>
        <CloseIcon fontSize="small" />
      </IconButton>
    </React.Fragment>
  );

  return (
    <div>
      <TriggerButton onClick={handleOpenAdd}>
        {option ? "Add Employee" : "Update Details"}
      </TriggerButton>

      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={openAdd}
        onClose={handleCloseAdd}
        closeAfterTransition
        slots={{ backdrop: StyledBackdrop }}
      >
        <Fade in={openAdd}>
          <ModalContent sx={style}>
            <div style={closeIconStyle}>
              <IconButton onClick={handleCloseAdd}>
                <CloseIcon />
              </IconButton>
            </div>
            <h2 id="transition-modal-title" className="modal-title">
              {option ? "Add Employee Details" : "Update Details"}
            </h2>
            <br />
            <p id="transition-modal-description" className="modal-description">
              <TextField
                id="outlined-basic"
                name="name"
                label="Enter Employees Name"
                variant="outlined"
                fullWidth
                onChange={handleChange}
                value={employeeDetails.name}
              />
            </p>
            <p>
              <TextField
                id="outlined-basic"
                name="salary"
                type="number"
                label="Enter Employees Salary"
                variant="outlined"
                fullWidth
                value={employeeDetails.salary}
                onChange={handleChange}
              />
            </p>
            <FormControlLabel
              required
              control={
                <Checkbox
                  checked={employeeDetails.isActive}
                  onChange={handleChange}
                  name="isActive"
                />
              }
              label="Is Active"
            />
            <div style={{ display: "flex", justifyContent: "space-evenly" }}>
              <Button variant="outlined" color="error" onClick={handleCloseAdd}>
                Cancel
              </Button>
              <Button variant="contained" color="success" onClick={handleSubmit}>
                Confirm
              </Button>
            </div>
          </ModalContent>
        </Fade>
      </Modal>
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleClose}
        message={option ? "Employee Added" : "Employee Updated"}
        action={action}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }} // Position at the bottom-left
      />
    </div>
  );
};


const Delete = ({ employeeID }) => {
  const [openDelete, setOpenDelete] = React.useState(false);
  const dispatch = useDispatch();

  const handleCloseDelete = () => setOpenDelete(false);

  const handleSubmit = () => {
    dispatch(deleteDetails(employeeID)); // Dispatch delete action
    dispatch(toggleSnackbar("Employee Deleted"));
    handleCloseDelete();
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === "clickaway") return;
    dispatch(toggleSnackbar(""));
  };

  const action = (
    <React.Fragment>
      <IconButton size="small" aria-label="close" color="inherit" onClick={handleCloseSnackbar}>
        <CloseIcon fontSize="small" />
      </IconButton>
    </React.Fragment>
  );

  return (
    <>
      <Button variant="contained" color="error" onClick={() => setOpenDelete(true)}>
        Delete
      </Button>

      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={openDelete}
        onClose={() => setOpenDelete(false)}
        closeAfterTransition
        slots={{ backdrop: StyledBackdrop }}
      >
        <Fade in={openDelete}>
          <ModalContent sx={style}>
            <div style={closeIconStyle}>
              <IconButton onClick={() => setOpenDelete(false)}>
                <CloseIcon />
              </IconButton>
            </div>
            <br />
            <h2 id="transition-modal-title" className="modal-title">
              Are You Sure You Want To Delete This Employee
            </h2>
            <br />
            <div style={{ display: "flex", justifyContent: "space-evenly" }}>
              <Button variant="outlined" onClick={() => setOpenDelete(false)}>
                Cancel
              </Button>
              <Button variant="contained" color="error" onClick={handleSubmit}>
                Confirm
              </Button>
            </div>
          </ModalContent>
        </Fade>
      </Modal>
      <Snackbar
