import React from "react";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { IconButton,FormControl,FormControlLabel,Checkbox,FormLabel} from "@mui/material";
import {  Visibility,VisibilityOff } from "@mui/icons-material";
import { InputLabel ,InputAdornment,OutlinedInput,RadioGroup,Radio} from "@mui/material";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import dayjs from "dayjs";

export default function SignUp() {
  const [showPassword, setShowPassword] = useState(false);
  const [data,setData]=useState({
    firstName:'',
    lastName:'',
    email:'',
    password:'',
    gender:''
  })
  const [check,setCheck]=useState(false)
  const [value, setValue] = useState(dayjs());

  const handledInputChange=(e)=>{
    const {name,value}=e.target
    setData((previousData)=>({
      ...previousData,
      [name]:value
    }))
  }
  const handleCheckChange=()=>{
    setCheck(true)
  }
  
  const navigate=useNavigate()
  const handleClickShowPassword = () => setShowPassword((show) => !show);

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  

  const LoginPage=()=>{
    if(data.password.length>=8 && (data.firstName && data.lastName && data.email && data.password && data.gender)){
        navigate('/login')
        const existingUsers =JSON.parse(localStorage.getItem('Users')) || [];
        const updatedUers=[...existingUsers,data]
        localStorage.setItem('Users',JSON.stringify(updatedUers))
        console.log(data)
    }else{
        setData({
          ...data,
          password:'',
        })
    }
  }


  return (
    <>
      <Box
      component="form"
      sx={{
      height:600,
      width:480,
      my:10,
      mx:60,
      display:"flex",
      flexDirection:"column",
      alignItems:"center",
      justifyContent:"center",
      border: '2px solid grey',
      borderRadius:'20px',
      bgcolor:'#cfd8dc' }}
      >
        <div>
            <h1>Sign Up</h1>
            <p>It's Quick And Easy</p>
        </div>
        <div>
          <TextField
            id="outline-required"
            label="First-Name"
            name='firstName'
            onChange={handledInputChange}
            value={data.firstName}
            // defaultValue="Hello World"
          />
          <TextField
            id="outline-required"
            label="Last Name"
            name="lastName"
            onChange={handledInputChange}
            value={data.lastName}
            // defaultValue="Hello World"
          />
          <br />
          <FormControl sx={{ margin:'10px 2px 10px 2px', width: "48ch" }}>
            <TextField
              id="outlined-email-input"
              label="Email Address"
              name="email"
              value={data.email}
              onChange={handledInputChange}
            />
          </FormControl>
          <br />
          <FormControl sx={{ margin:'10px 2px 10px 2px', width: "48ch" }} variant="outlined">
            <InputLabel htmlFor="outlined-adornment-password" >
              New Password
            </InputLabel>
            <OutlinedInput
              id="outlined-adornment-password"
              name="password"
              value={data.password} onChange={handledInputChange}
              type={showPassword ? "text" : "password"}
              endAdornment={
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              }
              label="New Password"
            />
          </FormControl>
        </div>
      
      <div>
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
                sx={{margin:'10px 2px 10px 2px',width:'48ch'}}
              label="Date of Birth"
              value={value}
              onChange={(newValue) => setValue(newValue)}
            />
        </LocalizationProvider>
      </div>
      <FormLabel id="demo-radio-buttons-group-label" ></FormLabel>
      <RadioGroup
        row
        aria-labelledby="demo-radio-buttons-group-label"
        value={data.gender}
        onChange={handledInputChange}
        name="gender"
      >
        <FormControlLabel value="female" control={<Radio />} label="Female" />
        <FormControlLabel value="male" control={<Radio />} label="Male" />
      </RadioGroup>
      
      <FormControlLabel required control={<Checkbox />} label="I Agree to Terms and Conditions" value={check} onClick={handleCheckChange}/>
      <Button variant="contained" sx={{margin:'10px 2px 10px 2px',width:'50ch'}} onClick={()=>{LoginPage()}} >
        Submit Details
      </Button>
      </Box>
    </>
  );
}
