import React from "react";
import PropTypes from "prop-types";
import { styled, css } from "@mui/system";
import { Modal as BaseModal } from "@mui/base/Modal";
import Fade from "@mui/material/Fade";
import { Button, TextField, Snackbar, IconButton, Checkbox, FormControlLabel } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import { useDispatch,useSelector } from "react-redux";
import { addDetails, deleteDetails,updateDetails, showSnackbar, hideSnackbar } from "../pages/employeeSlice";
import { v4 as uuidv4 } from "uuid";

export const Add = ({ option, employeeID, isActive, initialEmployeeDetails }) => {
  const [openAdd, setOpenAdd] = React.useState(false);
  const [employeeDetails, setEmployeeDetails] = React.useState({
    id: null,
    SrNo: 0,
    name: "",
    salary: "",
    isActive: isActive,
  });
  const dispatch = useDispatch();
  const snackbar = useSelector((state) => state.employee.snackbar);
  const handleOpenAdd = () => {
    setOpenAdd(true);
    if (!option && initialEmployeeDetails) {
      setEmployeeDetails(initialEmployeeDetails);
    }
  };
  const handleCloseAdd = () => setOpenAdd(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEmployeeDetails({
      ...employeeDetails,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = () => {
    const newEmployeeDetail = { ...employeeDetails, id: uuidv4() };
    if (option) {
      dispatch(addDetails(newEmployeeDetail));
      dispatch(showSnackbar("Employee Added"));
    } else {
      dispatch(updateDetails({ id: employeeID, updatedEmployee: newEmployeeDetail }));
      dispatch(showSnackbar("Employee Updated"));
    }
    setEmployeeDetails({
      SrNo: 0,
      id: uuidv4(),
      name: "",
      salary: "",
      isActive: isActive,
    });
    handleCloseAdd();
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') return;
    dispatch(hideSnackbar());
  };

  return (
    <div>
      <TriggerButton onClick={handleOpenAdd}>
        {option ? "Add Employee" : "Update Details"}
      </TriggerButton>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        message={snackbar.message}
        action={
          <React.Fragment>
            <Button color="secondary" size="small" onClick={handleCloseSnackbar}>
              UNDO
            </Button>
            <IconButton size="small" aria-label="close" color="inherit" onClick={handleCloseSnackbar}>
              <CloseIcon fontSize="small" />
            </IconButton>
          </React.Fragment>
        }
      />
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={openAdd}
        onClose={handleCloseAdd}
        closeAfterTransition
        slots={{ backdrop: StyledBackdrop }}
      >
        <Fade in={openAdd}>
          <ModalContent sx={style}>
            <div style={closeIconStyle}>
              <IconButton onClick={handleCloseAdd}>
                <CloseIcon />
              </IconButton>
            </div>
            <h2 id="transition-modal-title" className="modal-title">
              {option ? "Add Employee Details" : "Update Details"}
            </h2>
            <br />
            <TextField
              id="outlined-basic"
              name="name"
              label="Enter Employee's Name"
              variant="outlined"
              fullWidth
              onChange={handleChange}
              value={employeeDetails.name}
            />
            <TextField
              id="outlined-basic"
              name="salary"
              type="number"
              label="Enter Employee's Salary"
              variant="outlined"
              fullWidth
              value={employeeDetails.salary}
              onChange={handleChange}
            />
            <FormControlLabel
              required
              control={
                <Checkbox
                  checked={employeeDetails.isActive}
                  onChange={handleChange}
                  name="isActive"
                />
              }
              label="Is Active"
            />
            <div style={{ display: "flex", justifyContent: "space-evenly" }}>
              <Button variant="outlined" color="error" onClick={handleCloseAdd}>
                Cancel
              </Button>
              <Button variant="contained" color="success" onClick={handleSubmit}>
                Confirm
              </Button>
            </div>
          </ModalContent>
        </Fade>
      </Modal>
    </div>
  );
};

export const Delete = ({ employeeID }) => {
  const [openDelete, setOpenDelete] = React.useState(false);
  const dispatch = useDispatch();
  const snackbar = useSelector((state) => state.employee.snackbar);

  const handleCloseDelete = () => setOpenDelete(false);

  const handleSubmit = () => {
    dispatch(deleteDetails(employeeID));
    dispatch(showSnackbar("Employee Deleted"));
    handleCloseDelete();
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === "clickaway") return;
    dispatch(hideSnackbar());
  };

  return (
    <>
      <TriggerButton onClick={() => setOpenDelete(true)}>Delete</TriggerButton>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        message="Employee Deleted"
        action={
          <React.Fragment>
            <Button color="secondary" size="small" onClick={handleCloseSnackbar}>
              CLOSE
            </Button>
          </React.Fragment>
        }
      />
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={openDelete}
        onClose={handleCloseDelete}
        closeAfterTransition
        slots={{ backdrop: StyledBackdrop }}
      >
        <Fade in={openDelete}>
          <ModalContent sx={style}>
            <div style={closeIconStyle}>
              <IconButton onClick={handleCloseDelete}>
                <CloseIcon />
              </IconButton>
            </div>
            <h2 id="transition-modal-title" className="modal-title">
              Are You Sure You Want To Delete This Employee?
            </h2>
            <div style={{ display: "flex", justifyContent: "space-evenly" }}>
              <Button variant="outlined" onClick={handleCloseDelete}>
                Cancel
              </Button>
              <Button variant="contained" color="error" onClick={handleSubmit}>
                Confirm
              </Button>
            </div>
          </ModalContent>
        </Fade>
      </Modal>
    </>
  );
};

const closeIconStyle = {
  position: "absolute",
  top: 8,
  right: 8,
};

const Backdrop = React.forwardRef((props, ref) => {
  const { open, ...other } = props;
  return (
    <Fade in={open}>
      <div ref={ref} {...other} />
    </Fade>
  );
});

Backdrop.propTypes = {
  open: PropTypes.bool,
};

Add.propTypes = {
  option: PropTypes.bool.isRequired,
  employeeID: PropTypes.string,
  isActive: PropTypes.bool.isRequired,
  initialEmployeeDetails: PropTypes.object,
};

Delete.propTypes = {
  employeeID: PropTypes.string.isRequired,
};

const grey = {
  50: "#F3F6F9",
  100: "#E5EAF2",
  200: "#DAE2ED",
  300: "#C7D0DD",
  400: "#B0B8C4",
  500: "#9DA8B7",
  600: "#6B7A90",
  700: "#434D5B",
  800: "#303740",
  900: "#1C2025",
};

const Modal = styled(BaseModal)`
  position: fixed;
  z-index: 1300;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const StyledBackdrop = styled(Backdrop)`
  z-index: -1;
  position: fixed;
