// Layout.js
import React from "react";
import { Box, AppBar, Toolbar, IconButton, Drawer, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Avatar, Menu, MenuItem } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { deepOrange } from "@mui/material/colors";
import DashboardIcon from '@mui/icons-material/Dashboard';
import InfoIcon from '@mui/icons-material/Info';
import CallIcon from '@mui/icons-material/Call';
import logo from '../logo.png';

const drawerWidth = 200;

const Layout = ({ children, activeItem, setActiveItem }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [user, setUser] = React.useState(null);

  const open = Boolean(anchorEl);
  const navigate = useNavigate();

  React.useEffect(() => {
    const currentUser = JSON.parse(localStorage.getItem("currentUser"));
    if (currentUser) {
      setUser(currentUser);
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };
  
  const handleLogout = () => {
    localStorage.removeItem("currentUser");
    navigate("/login");
  };
  
  const handleClose = () => {
    setAnchorEl(null);
  };
  
  const handleItemClick = (text) => {
    setActiveItem(text);
    if (text === "Employee") {
      navigate("/employee");
    } else if (text === "Contact Us") {
      navigate("/contactus");
    } else {
      navigate("/");
    }
  };

  const icons = [DashboardIcon, InfoIcon, CallIcon];

  const getIconByIndex = (index) => {
    const Icon = icons[index % icons.length];
    return <Icon />;
  };

  return (
    <div>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
          <Toolbar style={{ display: "flex", justifyContent: "space-between" }}>
            <a href="./home">
              <img
                src={logo}
                alt="Logo"
                style={{ height: "50px", width: "auto", paddingLeft: "20px" }}
              />
            </a>
            {user && (
              <div style={{ display: "flex", flexDirection: "row" }}>
                <IconButton size="large" onClick={handleMenu}>
                  <Avatar sx={{ bgcolor: deepOrange[500] }}>
                    {user.firstName.charAt(0).toUpperCase()}
                  </Avatar>
                  <span style={{ color: "white", fontFamily: "serif" }}>
                    {user.firstName.charAt(0).toUpperCase() + user.firstName.slice(1)}
                  </span>
                </IconButton>
                <Menu
                  id="basic-menu"
                  anchorEl={anchorEl}
                  open={open}
                  onClose={handleClose}
                  MenuListProps={{ "aria-labelledby": "basic-button" }}
                >
                  <MenuItem onClick={handleLogout}>Logout</MenuItem>
                </Menu>
              </div>
            )}
          </Toolbar>
        </AppBar>
      </Box>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: "auto" }}>
          <List>
            {["Dashboard", "Employee", "Contact Us"].map((text, index) => (
              <ListItem key={text} disablePadding onClick={() => handleItemClick(text)} selected={activeItem === text}>
                <ListItemButton>
                  <ListItemIcon>{getIconByIndex(index)}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </Box>
      </Drawer>
      <Box sx={{ paddingTop: 10, paddingLeft: 30 }}>
        {children}
      </Box>
    </div>
  );
};

export default Layout;
