import { Button } from "@mui/material";
import { createSlice } from "@reduxjs/toolkit";
import { Delete, Add} from "../components/PopUps";


const initialState={
    value:'',
    columns :[
        { field: 'id', headerName: 'Sr.No.', width: 70,headerAlign:'center', align:'center' },
        { field: 'firstName', headerName: 'Name', width: 130, headerAlign:'center',align:'center' },
        { field: 'salary', headerName: 'Salary', type: 'number', width: 130,headerAlign:'center', align:'center' },
        {
          field: 'action',
          headerName: 'Action',
          type: 'number',
          width: 300,
          headerAlign:'center',
          align:'center',
          renderCell: () => (
            <Button
              variant="contained"
              color="primary"
            //   onClick={() => handleAction(params.row)}
            >
              <Add />
              <Delete/>
            </Button>
          )
        },
      ],
      
    rows :[],
}
export const employeeSlice=createSlice({
    name:'Work',
    initialState,
    reducers:{
      addDetails: (state, action) => {
        state.rows.push(action.payload);
        console.log(state)
      },
      deleteDetails: (state, action) => {
        state.rows = state.rows.filter(row => row.id !== action.payload.id);
      }
    }
})
export const {addDetails,deleteDetails}=employeeSlice.actions
export default employeeSlice.reducer
