import React from "react";
import logo from "../logo.png";
import {
  Avatar,
  Menu,
  MenuItem,
  Box,
  AppBar,
  Toolbar,
  IconButton,
  Drawer,
  List,ListItem,ListItemButton,ListItemIcon,Divider,ListItemText
} from "@mui/material";
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Cards from "./Cards";

const drawerWidth = 240;


export default function Home() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [user, setUser] = React.useState(null);
  
  const open = Boolean(anchorEl);
  const navigate = useNavigate();
  useEffect(() => {
    const currentUser = JSON.parse(localStorage.getItem('currentUser'));
    
    if (currentUser) {
      setUser(currentUser);
    }else{
      navigate('/login')
    }
  }, [navigate]);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleLogout = () => {
    localStorage.removeItem('currentUser')
    navigate("/login");
  };
  const handleClose = () => {
    setAnchorEl(null);
  };


  return (
    <div>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
          <Toolbar style={{ display: "flex", justifyContent: "space-between" }}>
            <img
              src={logo}
              alt="Logo"
              style={{
                height: "50px",
                width: "auto",
                paddingLeft: "20px",
              }}
            />
            { user &&(
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                }}
              >
                <IconButton
                  size="large"
                  onClick={handleMenu}
                >
                  <Avatar>{user.firstName.charAt(0).toUpperCase()}</Avatar>
                  <span style={{color:'white'}}>{user.firstName}</span>
                </IconButton>
                <Menu
                  id="basic-menu"
                  anchorEl={anchorEl}
                  open={open}
                  onClose={handleClose}
                  MenuListProps={{
                    "aria-labelledby": "basic-button",
                  }}
                >
                  <MenuItem onClick={handleLogout}>Logout</MenuItem>
                </Menu>
              </div>
            )}
          </Toolbar>
        </AppBar>
      </Box>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: 'auto' }}>
          <List>
            {['DashBoard', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
              <ListItem key={text} disablePadding>
                <ListItemButton>
                  <ListItemIcon>
                    {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                  </ListItemIcon>
                  <ListItemText primary={text} />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
          <Divider />
        </Box>
      </Drawer>
      <Cards/>
    </div>
  );
}
