import React, { useEffect } from "react";
import logo from "../logo.png";
import {
  Avatar,
  Menu,
  MenuItem,
  Box,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
} from "@mui/material";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [out, setOut] = React.useState(true);
  const [data, setData] = React.useState({
    firstName: '',
  });

  const open = Boolean(anchorEl);
  const navigate = useNavigate();

  useEffect(() => {
    const existingUsers = JSON.parse(localStorage.getItem("Users")) || [];
    const currentUser = existingUsers.find(user => user.isLoggedIn);
    if (currentUser) {
      setData({ firstName: currentUser.firstName });
    }
  }, []);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleLogout = () => {
    if (out) {
      navigate("/login");
      const existingUsers = JSON.parse(localStorage.getItem("Users")) || [];
      const userIndex = existingUsers.findIndex(
        (user) => user.firstName === data.firstName
      );
      if (userIndex !== -1) {
        existingUsers[userIndex].isLoggedIn = false;
        localStorage.setItem("Users", JSON.stringify(existingUsers));
      }
    }
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar
          sx={{
            display: "flex",
            justifyContent: "space-between",
            padding: { xs: "0 10px", sm: "0 20px" },
          }}
        >
          <Box
            component="img"
            src={logo}
            alt="Logo"
            sx={{
              height: { xs: "40px", sm: "50px" },
              width: "auto",
              paddingLeft: { xs: "10px", sm: "20px" },
            }}
          />

          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              paddingRight: { xs: "10px", sm: "20px" },
            }}
          >
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleMenu}
              color="inherit"
              sx={{ padding: 0 }}
            >
              <Avatar>{data.firstName.charAt(0)}</Avatar>
            </IconButton>
            <Typography
              variant="h6"
              noWrap
              sx={{ display: { xs: "none", sm: "block" }, color: "white", paddingLeft: "10px" }}
            >
              {data.firstName}
            </Typography>

            <Menu
              id="basic-menu"
              anchorEl={anchorEl}
              open={open}
              onClose={handleClose}
              MenuListProps={{
                "aria-labelledby": "basic-button",
              }}
            >
              <MenuItem onClick={handleLogout}>Logout</MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </AppBar>
    </Box>
  );
}
