import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { styled, css } from "@mui/system";
import { Modal as BaseModal } from "@mui/base/Modal";
import Fade from "@mui/material/Fade";
import { Button,TextField } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import { IconButton } from "@mui/material";
import Checkbox from "@mui/material/Checkbox";
import { FormControlLabel } from "@mui/material";
import { useDispatch } from "react-redux";
import {
  addDetails,
  deleteDetails,
  updateDetails
} from "../pages/employeeSlice";
import { employeeList} from '../pages/employeeSlice';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import  axios  from 'axios';



export const Add = ({
  option,
  employeeID,
  isActive,
  initialEmployeeDetails,
}) => {

  const [openAdd, setOpenAdd] = React.useState(false);
  const [employeeDetails, setEmployeeDetails] = React.useState({
    name: "",
    salary: "",
    isActive: isActive,
  });
  const dispatch = useDispatch();
  

  const handleOpenAdd = () => {
    // console.log(initialEmployeeDetails)
    setOpenAdd(true);
     !option && initialEmployeeDetails && (
      setEmployeeDetails(initialEmployeeDetails)
      // console.log(employeeDetails)
     )
  };
  const handleCloseAdd = () => setOpenAdd(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEmployeeDetails({
      ...employeeDetails,
      [name]: type === "checkbox" ? checked : value,
    });
    // console.log(employeeDetails);
  };

  useEffect(()=>{
    getData()
  },[])

  const getData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/read");
      const dataWithId =response.data.map((employee)=>({
        ...employee
      }))
      dispatch(employeeList(dataWithId))
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  
  const handleSubmit = async () => {
    try{
      if(option){
        const response = await axios.post('http://localhost:8000/create',employeeDetails)
        const newEmployee=response.data;
        dispatch(addDetails(newEmployee))
      }else {
        await axios.patch(`http://localhost:8000/update/${employeeID}`, employeeDetails);
        dispatch(updateDetails({ id: employeeID, updatedEmployee: employeeDetails }));
      }
    } catch (error) {
      console.error('Error submitting data:', error);
    }
    setEmployeeDetails({
      name: "",
      salary: "",
      isActive: isActive,
    })
    getData()
    // console.log(employeeDetails);
    
    handleCloseAdd();
   
  };

  return (
    <div>
      
      <div>
        <TriggerButton onClick={handleOpenAdd} sx={{color:'#2196f3'}}>
          {option ? "Add Employee" : <EditIcon/>}
        </TriggerButton>
        
        <Modal
          aria-labelledby="transition-modal-title"
          aria-describedby="transition-modal-description"
          open={openAdd}
          onClose={handleCloseAdd}
          closeAfterTransition
          slots={{ backdrop: StyledBackdrop }}
        >
          <Fade in={openAdd}>
            <ModalContent sx={style}>
              <div style={closeIconStyle}>
                <IconButton onClick={handleCloseAdd}>
                  <CloseIcon />
                </IconButton>
              </div>
              <h2 id="transition-modal-title" className="modal-title">
                {option ? "Add Employee Details" : "Update Details"}
              </h2>
              <br />
              <p
                id="transition-modal-description"
                className="modal-description"
              >
                <TextField
                  id="outlined-basic"
                  name="name"
                  label="Enter Employees Name"
                  variant="outlined"
                  fullWidth
                  onChange={handleChange}
                  value={employeeDetails.name}
                />
              </p>
              <p>
                <TextField
                  id="outlined-basic"
                  name="salary"
                  type="number"
                  label="Enter Employees Salary"
                  variant="outlined"
                  fullWidth
                  value={employeeDetails.salary}
                  onChange={handleChange}
                />
              </p>
              <FormControlLabel
                required
                control={
                  <Checkbox
                    checked={employeeDetails.isActive}
                    onChange={handleChange}
                    name="isActive"
                  />
                }
                label="Is Active"
                
              />
              <div style={{ display: "flex", justifyContent: "space-evenly" }}>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={handleCloseAdd}
                >
                  Cancel
                </Button>
                <Button
                  variant="contained"
                  color="success"
                  onClick={handleSubmit}
                >
                  Confirm
                </Button>
                
              </div>
            </ModalContent>
          </Fade>
        </Modal>
      </div>
    </div>
  );
};

export const Delete = ({ employeeID }) => {
  const [openDelete, setOpenDelete] = React.useState(false);
  // const [openSn, setOpenSn] = React.useState(false);
  // const [snackbarMessage, setSnackbarMessage] = React.useState("");
  const dispatch = useDispatch();
  const handleCloseDelete = () => setOpenDelete(false);
  useEffect(()=>{
    getData()
  },[])

  const getData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/read");
      const dataWithId =response.data.map((employee)=>({
        ...employee
      }))
      dispatch(employeeList(dataWithId))
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  const handleSubmitDel = async() => {
    console.log(`Deleting employee with ID: ${employeeID}`);
    try {
      await axios.delete(`http://localhost:8000/delete/${employeeID}`);
      dispatch(deleteDetails()); 
      handleCloseDelete();
    } catch (error) {
        console.error('Error deleting employee:', error); // Add error handling
    }
    getData()
};

  return (
    <>
      <TriggerButton onClick={() => setOpenDelete(true)} sx={{color:'#ef5350'}}><DeleteIcon /> </TriggerButton>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={openDelete}
        onClose={() => setOpenDelete(false)}
        closeAfterTransition
        slots={{ backdrop: StyledBackdrop }}
      >
        <Fade in={openDelete}>
          <ModalContent sx={style}>
            <div style={closeIconStyle}>
              <IconButton onClick={() => setOpenDelete(false)}>
                <CloseIcon />
              </IconButton>
            </div>
            <br />
            <h2 id="transition-modal-title" className="modal-title">
              Are You Sure You Want To Delete This Employee
            </h2>
            <br />
            <div style={{ display: "flex", justifyContent: "space-evenly" }}>
              <Button variant="outlined" onClick={() => setOpenDelete(false)}>
                Cancel
              </Button>
              <Button variant="contained" color="error" onClick={handleSubmitDel}>
                Confirm
              </Button>
            </div>
          </ModalContent>
        </Fade>
      </Modal>
      
    </>
  );
};
const closeIconStyle = {
  position: "absolute",
  top: 8,
  right: 8,
};

const Backdrop = React.forwardRef((props, ref) => {
  const { open, ...other } = props;
  return (
    <Fade in={open}>
      <div ref={ref} {...other} />
    </Fade>
  );
});

