import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { IconButton,FormControl } from "@mui/material";
import { Visibility,VisibilityOff } from "@mui/icons-material";
import { InputLabel ,InputAdornment,OutlinedInput,Divider} from "@mui/material";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

export default function SignUp() {
  const [showPassword, setShowPassword] = useState(false);
  const [data,setData]=useState({
    email:'',
    password:''
  })
  const navigate=useNavigate()

  const handledInputChange=(e)=>{
    const {name,value}=e.target
    setData((previousData)=>({
      ...previousData,
      [name]:value
    }))
    // console.log(data)
  }
  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  const signUpPage=()=>{
    navigate('/signup')
  }
  const homePage=()=>{
    // const User={email:state.email, password:state.password}
    // console.log(User)
    const existingUsers =JSON.parse(localStorage.getItem('Users')) || [];
    console.log(existingUsers)
    const userIndex = existingUsers.find(user => 
      user.email === data.email && user.password === data.password
    );
    if(userIndex){
      navigate('/home')
    }else{
      console.log("first")
    }
  }
  return (
    <div>
      <Box 
      component="form"
      sx={{
      height:380,
      width:480,
      my:'auto',
      mx:'auto',
      display:"flex",
      flexDirection:"column",
      alignItems:"center",
      justifyContent:"center",
      border: '2px solid grey',
      borderRadius:'20px',
      bgcolor:'#cfd8dc' }}>
        <div>
          <h3>Log In</h3>
        <FormControl sx={{ marginLeft: "20px", marginTop:"10px",width: "50ch" }} >
          <TextField
            id="outlined-email-input"
            label="Email Address"
            name="email"
            value={data.email}
            onChange={handledInputChange}
          />
        </FormControl>
        <FormControl sx={{ marginLeft: "20px", marginTop:"10px",width: "50ch" }} variant="outlined" >
          <InputLabel htmlFor="outlined-adornment-password">
            Password
          </InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            name="password"
            value={data.password}
            onChange={handledInputChange}
            type={showPassword ? "text" : "password"}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
        </FormControl>
        <Button variant="contained" sx={{ marginLeft: "35px", marginTop:"15px",width: "52ch",borderRadius:'20px' }}
        onClick={()=>homePage()}>
          Login
        </Button>
        <p style={{display:"flex",justifyContent:"center",alignItems:"flex-start", color:'blue',marginBottom:'10px',marginTop:'5px'}}>Forgot Password?</p>
          <Divider></Divider>
        <Button variant="contained" sx={{ marginLeft: '35px', marginTop:'30px',marginBottom:"10px", width: "52ch",borderRadius:'20px',bgcolor:'#81c784' }}
        onClick={()=>{signUpPage()}}>
          Create New Account
        </Button>
        </div>
      </Box>
    </div>
  );
}
