import React from "react";
import PropTypes from "prop-types";
import { styled, css } from "@mui/system";
import { Modal as BaseModal } from "@mui/base/Modal";
import Fade from "@mui/material/Fade";
import { Button, TextField } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import { IconButton } from "@mui/material";
import Checkbox from "@mui/material/Checkbox";
import { FormControlLabel } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import {
  addDetails,
  deleteDetails,
  updateDetails,
  hideSnackbar,
  showSnackbar
} from "../pages/employeeSlice";
import { v4 as uuidv4 } from "uuid";
import Snackbar from "@mui/material/Snackbar";

export const Add = ({
  option,
  employeeID,
  isActive,
  initialEmployeeDetails,
}) => {

  const [openAdd, setOpenAdd] = React.useState(false);
  const [employeeDetails, setEmployeeDetails] = React.useState({
    id: null,
    SrNo: 0,
    name: "",
    salary: "",
    isActive: isActive,
  });
  const dispatch = useDispatch();
  const snackbar = useSelector((state) => state.employee.snackbar);
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') return;
    dispatch(hideSnackbar())
  }
    

  const handleOpenAdd = () => {
    // console.log(initialEmployeeDetails)
    setOpenAdd(true);
     !option && initialEmployeeDetails && (
      setEmployeeDetails(initialEmployeeDetails)
      // console.log(employeeDetails)
     )
  };
  const handleCloseAdd = () => setOpenAdd(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEmployeeDetails({
      ...employeeDetails,
      [name]: type === "checkbox" ? checked : value,
    });
    // console.log(employeeDetails);
  };

  const handleSubmit = () => {
    const newEmployeeDetail = { ...employeeDetails, id: uuidv4() };
    if (option) {
      dispatch(addDetails(newEmployeeDetail));
      dispatch(showSnackbar("Employee Added"));
      
    } else {
      dispatch(updateDetails({ id: employeeID, updatedEmployee: newEmployeeDetail }));
      dispatch(showSnackbar("Employee Updated"));
    }
    setEmployeeDetails({
      SrNo: 0,
      id: uuidv4(),
      name: "",
      salary: "",
      isActive: isActive,
    });
    handleCloseAdd();
  };
  const action = (
    <React.Fragment>
      <Button color="secondary" size="small" onClick={handleClose}>
        UNDO
      </Button>
      <IconButton
        size="small"
        aria-label="close"
        color="inherit"
        onClick={handleClose}
      >
        <CloseIcon fontSize="small" />
      </IconButton>
    </React.Fragment>
  );

  return (
    <div>
      
      <div>
        <TriggerButton onClick={handleOpenAdd}>
          {option ? "Add Employee" : "Update Details"}
        </TriggerButton>
        <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleClose}
        message={snackbar.message}
        action={action}
      />
        <Modal
          aria-labelledby="transition-modal-title"
          aria-describedby="transition-modal-description"
          open={openAdd}
          onClose={handleCloseAdd}
          closeAfterTransition
          slots={{ backdrop: StyledBackdrop }}
        >
          <Fade in={openAdd}>
            <ModalContent sx={style}>
              <div style={closeIconStyle}>
                <IconButton onClick={handleCloseAdd}>
                  <CloseIcon />
                </IconButton>
              </div>
              <h2 id="transition-modal-title" className="modal-title">
                {option ? "Add Employee Details" : "Update Details"}
              </h2>
              <br />
              <p
                id="transition-modal-description"
                className="modal-description"
              >
                <TextField
                  id="outlined-basic"
                  name="name"
                  label="Enter Employees Name"
                  variant="outlined"
                  fullWidth
                  onChange={handleChange}
                  value={employeeDetails.name}
                />
              </p>
              <p>
                <TextField
                  id="outlined-basic"
                  name="salary"
                  type="number"
                  label="Enter Employees Salary"
                  variant="outlined"
                  fullWidth
                  value={employeeDetails.salary}
                  onChange={handleChange}
                />
              </p>
              <FormControlLabel
                required
                control={
                  <Checkbox
                    checked={employeeDetails.isActive}
                    onChange={handleChange}
                    name="isActive"
                  />
                }
                label="Is Active"
                
              />
              <div style={{ display: "flex", justifyContent: "space-evenly" }}>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={handleCloseAdd}
                >
                  Cancel
                </Button>
                <Button
                  variant="contained"
                  color="success"
                  onClick={handleSubmit}
                >
                  Confirm
                </Button>
                
              </div>
            </ModalContent>
          </Fade>
        </Modal>
      </div>
    </div>
  );
};

export const Delete = ({ employeeID }) => {
  const [openDelete, setOpenDelete] = React.useState(false);
  const dispatch = useDispatch();
  const snackbar = useSelector((state) => state.employee.snackbar);
  const handleCloseDelete = () => setOpenDelete(false);

  const handleSubmit = () => {
    dispatch(deleteDetails(employeeID)); // Dispatch delete action
    dispatch(showSnackbar("Employee Deleted"))
    handleCloseDelete();
  };

  const handleClose = (event, reason) => {
    if (reason === "clickaway") return;
    dispatch(hideSnackbar())
  };

  const action = (
    <React.Fragment>
      <Button color="secondary" size="small" onClick={()=>handleClose()}>
        CLOSE
      </Button>
    </React.Fragment>
  );

  return (
    <>
      <TriggerButton onClick={() => setOpenDelete(true)}>Delete</TriggerButton>
      <Snackbar
        open={snackbar.open} // Ensure Snackbar is controlled by 'opens' state
        autoHideDuration={6000}
        onClose={handleClose}
        message="Employee Deleted"
        action={action}
      />
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={openDelete}
        onClose={() => setOpenDelete(false)}
        closeAfterTransition
        slots={{ backdrop: StyledBackdrop }}
      >
        <Fade in={openDelete}>
          <ModalContent sx={style}>
            <div style={closeIconStyle}>
              <IconButton onClick={() => setOpenDelete(false)}>
                <CloseIcon />
              </IconButton>
            </div>
            <br />
            <h2 id="transition-modal-title" className="modal-title">
              Are You Sure You Want To Delete This Employee
            </h2>
            <br />
            <div style={{ display: "flex", justifyContent: "space-evenly" }}>
              <Button variant="outlined" onClick={() => setOpenDelete(false)}>
                Cancel
              </Button>
              <Button variant="contained" color="error" onClick={handleSubmit}>
                Confirm
              </Button>
            </div>
          </ModalContent>
        </Fade>
      </Modal>
    </>
  );
};
const closeIconStyle = {
  position: "absolute",
  top: 8,
  right: 8,
};

const Backdrop = React.forwardRef((props, ref) => {
  const { open, ...other } = props;
  return (
    <Fade in={open}>
      <div ref={ref} {...other} />
    </Fade>
  );
});

Backdrop.propTypes = {
  open: PropTypes.bool,
};

Add.propTypes = {
  option: PropTypes.bool.isRequired,
  employeeDetails: PropTypes.object.isRequired,
  setEmployeeDetails: PropTypes.func.isRequired,
};

const grey = {
  50: "#F3F6F9",
  100: "#E5EAF2",
  200: "#DAE2ED",
  300: "#C7D0DD",
  400: "#B0B8C4",
  500: "#9DA8B7",
  600: "#6B7A90",
  700: "#434D5B",
  800: "#303740",
